"use client";

import { Check, Zap } from "lucide-react";
import { useState, useId } from "react";
import { motion, AnimatePresence } from "motion/react";
import { cva, type VariantProps } from "class-variance-authority";

import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Separator } from "@/components/ui/separator";
import { type Plan } from "@/lib/billingsdk-config";
import { cn } from "@/lib/utils";

const sectionVariants = cva("py-32", {
  variants: {
    size: {
      small: "py-6 md:py-12",
      medium: "py-10 md:py-20",
      large: "py-16 md:py-32",
    },
    theme: {
      minimal: "",
      classic: "bg-gradient-to-b from-background to-muted/20 relative overflow-hidden",
    },
  },
  defaultVariants: {
    size: "medium",
    theme: "minimal",
  },
});

const titleVariants = cva("text-pretty text-left font-bold", {
  variants: {
    size: {
      small: "text-3xl lg:text-4xl",
      medium: "text-4xl lg:text-5xl",
      large: "text-4xl lg:text-6xl",
    },
    theme: {
      minimal: "",
      classic: "text-center bg-gradient-to-r from-foreground to-muted-foreground bg-clip-text text-transparent p-1",
    },
  },
  defaultVariants: {
    size: "large",
    theme: "minimal",
  },
});

const descriptionVariants = cva("text-muted-foreground max-w-3xl", {
  variants: {
    size: {
      small: "text-base lg:text-lg",
      medium: "text-lg lg:text-xl",
      large: "lg:text-xl",
    },
    theme: {
      minimal: "text-left",
      classic: "text-center mx-auto",
    },
  },
  defaultVariants: {
    size: "large",
    theme: "minimal",
  },
});

const cardVariants = cva(
  "relative overflow-hidden rounded-2xl border bg-gradient-to-br from-card via-card to-muted/20 p-8 shadow-lg transition-all duration-300 hover:shadow-xl hover:scale-[1.02] group",
  {
    variants: {
      size: {
        small: "p-4",
        medium: "p-6",
        large: "p-8",
      },
      theme: {
        minimal: "",
        classic: "hover:shadow-2xl backdrop-blur-sm bg-card/50 border-border/50",
      },
      highlight: {
        true: "",
        false: "",
      },
    },
    compoundVariants: [
      {
        theme: "classic",
        highlight: true,
        className: "ring-2 ring-primary/30 border-primary/40 bg-gradient-to-br from-primary/10 via-card to-secondary/5 relative overflow-hidden",
      },
      {
        theme: "minimal",
        highlight: true,
        className: "bg-gradient-to-br from-primary/5 via-card to-secondary/5 border-primary/20",
      },
    ],
    defaultVariants: {
      size: "large",
      theme: "minimal",
      highlight: false,
    },
  }
);

const priceTextVariants = cva("font-bold bg-gradient-to-r from-foreground to-muted-foreground bg-clip-text text-transparent", {
  variants: {
    size: {
      small: "text-4xl",
      medium: "text-5xl",
      large: "text-6xl",
    },
    theme: {
      minimal: "",
      classic: "text-6xl font-extrabold bg-gradient-to-r from-primary via-foreground to-secondary bg-clip-text text-transparent",
    },
  },
  defaultVariants: {
    size: "large",
    theme: "minimal",
  },
});

const featureIconVariants = cva("", {
  variants: {
    size: {
      small: "size-4",
      medium: "size-5",
      large: "size-5",
    },
    theme: {
      minimal: "text-emerald-500",
      classic: "text-emerald-400",
    },
  },
  defaultVariants: {
    size: "large",
    theme: "minimal",
  },
});

const highlightBadgeVariants = cva("absolute -top-3 left-1/2 transform -translate-x-1/2 bg-gradient-to-r from-primary to-secondary text-primary-foreground px-4 py-1 rounded-full text-sm font-semibold shadow-lg", {
  variants: {
    theme: {
      minimal: "",
      classic: "bg-gradient-to-r from-primary via-primary/90 to-secondary text-primary-foreground border border-primary/20 shadow-xl",
    },
  },
  defaultVariants: {
    theme: "minimal",
  },
});

const toggleVariants = cva("flex h-12 w-fit shrink-0 items-center rounded-xl p-1 text-lg bg-gradient-to-r from-muted to-muted/80 shadow-inner", {
  variants: {
    theme: {
      minimal: "bg-muted",
      classic: "bg-gradient-to-r from-muted/50 to-muted/30 backdrop-blur-sm border border-border/50 shadow-lg",
    },
  },
  defaultVariants: {
    theme: "minimal",
  },
});

const buttonVariants = cva(
  "w-full bg-gradient-to-r from-primary to-secondary text-primary-foreground font-semibold py-3 px-6 rounded-xl transition-all duration-300 hover:shadow-lg hover:scale-105 active:scale-95 group relative overflow-hidden",
  {
    variants: {
      theme: {
        minimal: "shadow hover:shadow-lg",
        classic: "relative overflow-hidden bg-gradient-to-r from-primary via-primary/90 to-secondary text-primary-foreground font-bold py-4 px-8 rounded-xl hover:shadow-2xl active:scale-95 border border-primary/20",
      },
      highlight: {
        true: "ring-2 ring-primary/30 ring-offset-2 ring-offset-background",
        false: "",
      },
    },
    defaultVariants: {
      theme: "minimal",
      highlight: false,
    },
  }
);

interface PricingTableFourProps extends VariantProps<typeof sectionVariants> {
  className?: string;
  plans: Plan[];
  title?: string;
  description?: string;
  onPlanSelect?: (planId: string) => void;
}

export function PricingTableFour({ 
  className, 
  plans, 
  title, 
  description, 
  onPlanSelect, 
  size,
  theme = "minimal"
}: PricingTableFourProps) {
  const [isAnnually, setIsAnnually] = useState(false);
  const uniqueId = useId();

  function calculateDiscount(monthlyPrice: string, yearlyPrice: string): number {
    const monthly = parseFloat(monthlyPrice);
    const yearly = parseFloat(yearlyPrice);

    if (
      monthlyPrice.toLowerCase() === "custom" ||
      yearlyPrice.toLowerCase() === "custom" ||
      isNaN(monthly) ||
      isNaN(yearly) ||
      monthly === 0
    ) {
      return 0;
    }

    const discount = ((monthly * 12 - yearly) / (monthly * 12)) * 100;
    return Math.round(discount);
  }

  const yearlyPriceDiscount = plans.length
    ? Math.max(
      ...plans.map((plan) =>
        calculateDiscount(plan.monthlyPrice, plan.yearlyPrice)
      )
    )
    : 0;

  return (
    <section className={cn(sectionVariants({ size, theme }), className)}>
      {/* Background gradient effects */}
      <div className="absolute inset-0 bg-gradient-to-br from-primary/5 via-transparent to-secondary/5 pointer-events-none" />
      <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-primary/10 rounded-full blur-3xl pointer-events-none" />
      <div className="absolute bottom-1/4 right-1/4 w-64 h-64 bg-secondary/10 rounded-full blur-2xl pointer-events-none" />

      <div className={cn("container relative", "p-0 md:p-[1rem]")}>
        <div className="mx-auto flex max-w-7xl flex-col gap-8">
          <div className={cn("flex flex-col gap-6 text-center")}>
            <h2 className={cn(titleVariants({ size, theme }), "text-center bg-gradient-to-r from-foreground via-primary to-secondary bg-clip-text text-transparent")}>
              {title || "Choose Your Plan"}
            </h2>
            <p className={cn(descriptionVariants({ size, theme }), "text-center mx-auto")}>
              {description || "Select the perfect plan for your needs. Upgrade or downgrade anytime."}
            </p>
          </div>

          <div className="flex justify-center mb-8">
            <div className={cn(toggleVariants({ theme }))}>
              <RadioGroup
                defaultValue="monthly"
                className="h-full grid-cols-2"
                onValueChange={(value) => {
                  setIsAnnually(value === "annually");
                }}
              >
                <div className='has-[button[data-state="checked"]]:bg-background has-[button[data-state="checked"]]:shadow-md h-full rounded-lg transition-all'>
                  <RadioGroupItem
                    value="monthly"
                    id={`${uniqueId}-monthly`}
                    className="peer sr-only"
                  />
                  <Label
                    htmlFor={`${uniqueId}-monthly`}
                    className="text-muted-foreground peer-data-[state=checked]:text-primary flex h-full cursor-pointer items-center justify-center px-6 font-semibold transition-all hover:text-foreground"
                  >
                    Monthly
                  </Label>
                </div>
                <div className='has-[button[data-state="checked"]]:bg-background has-[button[data-state="checked"]]:shadow-md h-full rounded-lg transition-all'>
                  <RadioGroupItem
                    value="annually"
                    id={`${uniqueId}-annually`}
                    className="peer sr-only"
                  />
                  <Label
                    htmlFor={`${uniqueId}-annually`}
                    className="text-muted-foreground peer-data-[state=checked]:text-primary flex h-full cursor-pointer items-center justify-center gap-2 px-6 font-semibold transition-all hover:text-foreground"
                  >
                    Yearly
                    {yearlyPriceDiscount > 0 && (
                      <span className="ml-1 rounded-full bg-gradient-to-r from-emerald-500 to-emerald-600 px-2 py-0.5 text-xs text-white font-bold shadow-sm">
                        Save {yearlyPriceDiscount}%
                      </span>
                    )}
                  </Label>
                </div>
              </RadioGroup>
            </div>
          </div>

          <div className="grid gap-8 md:grid-cols-3 max-w-6xl mx-auto">
            {plans.map((plan, index) => (
              <motion.div
                key={plan.id}
                layout
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className={cn(
                  cardVariants({ 
                    size, 
                    theme, 
                    highlight: plan.highlight 
                  })
                )}
              >
                {/* Highlight badge for popular plan */}
                {plan.highlight && (
                  <div className={highlightBadgeVariants({ theme })}>
                    Most Popular
                  </div>
                )}

                {/* Gradient overlay for hover effect */}
                <div className="absolute inset-0 bg-gradient-to-br from-primary/0 via-transparent to-secondary/0 group-hover:from-primary/5 group-hover:to-secondary/5 transition-all duration-300 rounded-2xl" />

                <div className="relative z-10">
                  <div className="text-center mb-8">
                    <h3 className="text-2xl font-bold mb-2 bg-gradient-to-r from-foreground to-muted-foreground bg-clip-text text-transparent">
                      {plan.title}
                    </h3>

                    <AnimatePresence mode="wait">
                      <motion.div
                        key={isAnnually ? "year" : "month"}
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        transition={{ duration: 0.3 }}
                        className="mb-4"
                      >
                        {isAnnually ? (
                          <>
                            <div className="flex items-baseline justify-center gap-1">
                              <span className={cn(priceTextVariants({ size, theme }))}>
                                {parseFloat(plan.yearlyPrice) >= 0 && plan.currency}
                                {plan.yearlyPrice}
                              </span>
                            </div>
                            <p className="text-muted-foreground text-sm">per year</p>
                            {calculateDiscount(plan.monthlyPrice, plan.yearlyPrice) > 0 && (
                              <p className="text-emerald-500 text-sm font-semibold mt-1">
                                Save {calculateDiscount(plan.monthlyPrice, plan.yearlyPrice)}% annually
                              </p>
                            )}
                          </>
                        ) : (
                          <>
                            <div className="flex items-baseline justify-center gap-1">
                              <span className={cn(priceTextVariants({ size, theme }))}>
                                {parseFloat(plan.monthlyPrice) >= 0 && plan.currency}
                                {plan.monthlyPrice}
                              </span>
                            </div>
                            <p className="text-muted-foreground text-sm">per month</p>
                          </>
                        )}
                      </motion.div>
                    </AnimatePresence>
                  </div>

                  <Separator className="mb-8 bg-gradient-to-r from-transparent via-border to-transparent" />

                  <div className="space-y-6">
                    <ul className="space-y-4">
                      {plan.features.map((feature, featureIndex) => (
                        <motion.li 
                          key={featureIndex} 
                          className="flex items-center gap-3"
                          initial={{ opacity: 0, x: -10 }}
                          animate={{ opacity: 1, x: 0 }}
                          transition={{ duration: 0.3, delay: featureIndex * 0.05 }}
                        >
                          <div className="flex-shrink-0 w-5 h-5 rounded-full bg-gradient-to-r from-emerald-500 to-emerald-600 flex items-center justify-center">
                            <Check className={cn(featureIconVariants({ size, theme }), "text-white w-3 h-3")} />
                          </div>
                          <span className="text-foreground/90 text-sm">
                            {feature.name}
                          </span>
                        </motion.li>
                      ))}
                    </ul>

                    <Button
                      className={cn(buttonVariants({ theme, highlight: plan.highlight }))}
                      onClick={() => onPlanSelect?.(plan.id)}
                      aria-label={`Select ${plan.title} plan`}
                    >
                      <div className="absolute inset-0 bg-gradient-to-r from-white/0 via-white/20 to-white/0 translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-700" />
                      <span className="relative z-10 flex items-center gap-2">
                        {plan.highlight && <Zap className="w-4 h-4" />}
                        {plan.buttonText}
                      </span>
                    </Button>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

export interface Feature {
  name: string;
  included: boolean;
}

export interface Plan {
  id: string;
  title: string;
  monthlyPrice: string;
  yearlyPrice: string;
  currency: string;
  description: string;
  buttonText: string;
  highlight: boolean;
  features: Feature[];
}

export interface CurrentPlan {
  id: string;
  name: string;
  price: string;
  interval: string;
}

export const plans: Plan[] = [
  {
    id: "starter",
    title: "Starter",
    monthlyPrice: "9",
    yearlyPrice: "90",
    currency: "$",
    description: "Perfect for individuals and small projects",
    buttonText: "Get Started",
    highlight: false,
    features: [
      { name: "Up to 5 projects", included: true },
      { name: "10GB storage", included: true },
      { name: "Basic support", included: true },
      { name: "API access", included: false },
      { name: "Advanced analytics", included: false },
    ],
  },
  {
    id: "pro",
    title: "Pro",
    monthlyPrice: "29",
    yearlyPrice: "290",
    currency: "$",
    description: "Best for growing businesses and teams",
    buttonText: "Start Free Trial",
    highlight: true,
    features: [
      { name: "Unlimited projects", included: true },
      { name: "100GB storage", included: true },
      { name: "Priority support", included: true },
      { name: "API access", included: true },
      { name: "Advanced analytics", included: true },
    ],
  },
  {
    id: "enterprise",
    title: "Enterprise",
    monthlyPrice: "99",
    yearlyPrice: "990",
    currency: "$",
    description: "For large organizations with custom needs",
    buttonText: "Contact Sales",
    highlight: false,
    features: [
      { name: "Unlimited everything", included: true },
      { name: "1TB storage", included: true },
      { name: "24/7 dedicated support", included: true },
      { name: "Full API access", included: true },
      { name: "Custom integrations", included: true },
    ],
  },
];
